import "./App.css"
import PostAdd from './PostAdd';
import Post from './Post';
import {useState} from 'react';
function App() {
  const [isPostSubmitted, setIsPostSubmitted] = useState(false);
  const [postContent, setPostContent] = useState(null)
  const [onvanP, setOnvanP] = useState(null)
  const [date, setDate] = useState(null)
  const [age, setAge] = useState(null)

  function handleOnPostSubmitted(newPostContent, newOnvanP, newAge, newDate){
    setPostContent(newPostContent);
    setOnvanP(newOnvanP);
    setDate(newDate);
    setAge(newAge);
    setIsPostSubmitted(true);
  }

  return (
    <div className="container">
      {
        isPostSubmitted
          ? <Post onvanP={onvanP} age={age} date={date} content={postContent} />
          : <PostAdd onPostSubmitted={handleOnPostSubmitted} />
      }
    </div>
  );
}

export default App;
