import { useEffect, useState } from "react";
import { Outlet, useParams } from "react-router-dom";

export default function SearchUsers(){
    const { query } = useParams();
    const [users, setUsers] = useState([])

    useEffect(()=>{
        const foundUsers = [];
        // ✌️✌️✌️✌️✌️

        fetch("https://jsonplaceholder.typicode.com/users").then((res) => res.json()).then((res)=>{
            for(const user of res){
                if(user.username.includes(query) || user.name.includes(query)) {
                    foundUsers.push({
                        id: user.id,
                        name: user.name.replace(new RegExp(`${query}`, 'g'), `<mark>${query}</mark>`),
                        username: user.username.replace(new RegExp(`${query}`, 'g'), `<mark>${query}</mark>`)
                    });
                }
            }

            setUsers(foundUsers)
        })
    }, [])

    return (
        <div>
            <h2>
                Users Result Page
            </h2>
            {users.map((user)=><div key={user.id}>
                <h3 dangerouslySetInnerHTML={{__html: user.name}} /><p dangerouslySetInnerHTML={{__html: user.name}} />
            </div>)}
        </div>
    );
}
