import { useState } from "react";

function PostAdd({onPostSubmitted}){
    const [valueOnvan, setValueOnvan] = useState('')
    const [valueAge, setValueAge] = useState('')
    const [valueDate, setValueDate] = useState('')
    const [valueTextArea, setValueTextArea] = useState('')

    const [isTextAreaIncludesError, setIsTextAreaIncludesError] = useState(false)
    
    function handleSubmit(e){
        e.preventDefault();
        if(valueTextArea === '') {
            setIsTextAreaIncludesError(true);
            return;
        }

        onPostSubmitted(valueTextArea, valueOnvan, valueAge, valueDate)
    }

    function handleOnvanChange(e){
        setValueOnvan(e.target.value);
    }

    function handleDateChange(e){
        setValueDate(e.target.value);
    }

    function handleAgeChange(e){
        if(Number(e.target.value) >= 0 && Number(e.target.value) < 100) {
            setValueAge(e.target.value);
        }
    }

    function handleTAChange(e){
        if(e.target.value.length <= 5) {
            setValueTextArea(e.target.value);
        }
    }

    func

    return (<form onSubmit={handleSubmit}>
        <label className="item" htmlFor="onvan">Onvan</label>
        <input value={valueOnvan} onChange={handleOnvanChange} name="onvan" id="onvan" type="text" />
<br/>
        <label className="item" htmlFor="age">Age</label>
        <input value={valueAge} onChange={handleAgeChange} name="age" id="age" type="text" />
<br/>
        <label className="item" htmlFor="date">Date</label>
        <input value={valueDate} onChange={handleDateChange} name="date" id="date" type="date" />
<br/>
        <label className="item" htmlFor="date">writer</label>
        <input value={valueWriter} onChange={handleWriterChange} name="name" id="name" type="text" />

<br/>
        <textarea className="item" value={valueTextArea} onChange={handleTAChange} name="ta" />
        <span>{valueTextArea.length} / 5</span>
        {isTextAreaIncludesError ? <span>WFT?</span> : null}
<br/>
        <button className="item" type="submit">
            Submit post
        </button>
    </form>);
}

export default PostAdd;
