import "./App.css";
import { useEffect, useState } from "react";
import Heading from "./heading/Heading";
import Post from "./component/Post";
import { useRef } from "react";

function App() {
  const [postList, setPostList] = useState([]);
  const [isLoading, setIsLoading] = useState(true);
  const [bookmarkId, setBookmarkId] = useState(null);
  const [searchText, setSearchText] = useState(null)
  const inp = useRef(null)
const placeHolder = localStorage.setItem( 'placeHolder'  , fetch("https://jsonplaceholder.typicode.com/posts").then((res) => res.json()).then((res) => {
  setPostList(res)
  setIsLoading(false)
}))
  useEffect(() => {
      const p = localStorage.getItem('p') === 'true';
      const user = p ? localStorage.getItem('user') : '';
      this.setState({ user, p });

  })


  function search(){
    setSearchText(inp.current.value)
  }


  return (
    <div dir="rtl">
      <Heading />
      {isLoading ? <div className="spinner" /> : null}

      <div>
          <input ref={inp} placeholder="Search something..." />
          <button onClick={search} >search</button>
      </div>

      {postList.map((post) =>
        <Post
          id={post.id}
          title={post.title}
          body={post.body}
          bookmarkId={bookmarkId}
          setBookmarkId={setBookmarkId}
          searchText={searchText}
        />)}


    </div>
  );
}
export default App
