import "./App.css"
import PostAdd from './PostAdd';
import Post from './Post';
import {useState} from 'react';
import Input from "./Input";
function App() {
  const [isPostSubmitted, setIsPostSubmitted] = useState(false);
  const [postContent, setPostContent] = useState(null)
  const [onvan, setOnvan] = useState(null)
  const [date, setDate] = useState(null)
  const [age, setAge] = useState(null)

  function handleOnPostSubmitted(newPostContent, newOnvan, newAge, newDate){
    setPostContent(newPostContent);
    setOnvan(newOnvan);
    setDate(newDate);
    setAge(newAge);
    setIsPostSubmitted(true);
  }

  return (
    <div className="container">
      {
        isPostSubmitted
          ? <Post onvan={onvan} age={age} date={date} content={postContent} />
          : <PostAdd onPostSubmitted={handleOnPostSubmitted} />
      }
    </div>
  );
}

export default App;
