import { useContext } from "react";
import "./App.css";
import Navbar from '../Nav/Navbar';
import Form from "../Form/Form";
import postsContext from "../postContext";

import { useState } from "react";
import AddContact from "./AddContact/AddContact";
import Box from "./Box";

// import Note from "./Notes/Note";

export default function Home(){

const value = useContext(postsContext)

    // const[name,setName]=useState("");
    // const[lastName , setLastName]=useState("");
    // const[email , setEmail]=useState("");
    // const[tel , setTel]=useState("");
    // const[age , setAge]= useState("");
    // const[gender , setGender]= useState("");
    // const[address , setAddress]= useState("");
    // const[postSubmited , setPostSubmited]= useState(false);

    const [notes, setNotes] = useState([]);

    function addContactAddContact(newNote){
      setNotes([...notes, newNote]);
    }
  
    
    function handleTable(newName , newLastName , newEmail ,newTel ,newAge , newGender , newAddress){
        setName(newName);
        setLastName(newLastName);
        setEmail(newEmail);
        setTel(newTel);
        setAge(newAge);
        setGender(newGender);
        setAddress(newAddress);
        setPostSubmited(true)

        
        localStorage.setItem(`newpost`, JSON.stringify({
            name: newName,
            lastName: newLastName,
            email: newEmail,
            tel: newTel,
            age:newAge,
            gender:newGender,
            address:newAddress,
          }))
          


    }


    return(
        <div value={{ notes, addContact }}>
            
      <h1>دفترچه تلفن</h1>

      <AddContact />

<Box />



<Navbar/>
{value.Name.map((names=><Table   name={names.name} lastName={names.lastName} tel={names.tel} email={names.email} gender={names.gender} age={names.age} address={names.address}/>
))}
<Form setPostSubmitted={setPostSubmited} onSubmitted={handleTable}/>

        </div>
    )
}
